import { useState } from "react";

const EventPractice = () => {
    const[form,setForm] = useState({
        username:'',
        message:''
    });

    const {username,message} = form;
    const onChange = e => {
        const nextForm ={
            ...form, [e.target.name]:e.target.value
        };
        setForm(nextForm);
    };
    const onClick = () => {
        alert(username + ": " + message);
        setForm({
            username:'',
            message:''
        });
    };
    const onKeyPress = e => {
        if(e.key === "Enter") {
            onClick();
        }
    };

    return (
        <div>
            <h1>Event Practice</h1>


        </div>
    )


}
